import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { CircularProgress, Box, Typography, Container, Grid, Card, CardContent, Button } from '@mui/material';
import { API_URL } from '../config.ts';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

interface StockData {
  date: string;
  close_price: number;
}

interface LPPLAIData {
  date: string;
  close_price: number;
  dtcai: number;
  color: string;
}

interface AssetData {
  symbol: string;
  name: string;
  data: StockData[];
}

const SP500Return: React.FC = () => {
  const [assetsData, setAssetsData] = useState<AssetData[]>([]);
  const [lpplData, setLpplData] = useState<LPPLAIData[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedAsset, setSelectedAsset] = useState<string | null>(null);

  const assets = [
    { symbol: '^GSPC', name: 'S&P 500' },
    { symbol: 'GC=F', name: 'Gold' },
    { symbol: '^TNX', name: 'Interest Rate' },
    { symbol: 'BTC-USD', name: 'Bitcoin' }
  ];

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        setError(null);

        // Fetch all assets data
        const assetsDataPromises = assets.map(async (asset) => {
          const response = await fetch(`${API_URL}/api/v1/stock-data/symbol/${asset.symbol}`);
          if (!response.ok) {
            throw new Error(`Failed to fetch ${asset.name} data`);
          }
          const data = await response.json();
          // Normalize the data once when received
          const normalizedData = data.map((item: StockData) => ({
            ...item,
            normalized_price: (item.close_price - Math.min(...data.map(d => d.close_price))) / 
                            (Math.max(...data.map(d => d.close_price)) - Math.min(...data.map(d => d.close_price)))
          }));
          return { ...asset, data: normalizedData };
        });

        const assetsData = await Promise.all(assetsDataPromises);
        setAssetsData(assetsData);

        // Fetch LPPL AI data
        const lpplResponse = await fetch(`${API_URL}/api/v1/lppl-ai-data/`);
        if (!lpplResponse.ok) {
          throw new Error('Failed to fetch LPPL AI data');
        }
        const lpplAiData = await lpplResponse.json();
        // Normalize LPPL AI data once when received
        const normalizedLpplData = lpplAiData.map((item: LPPLAIData) => ({
          ...item,
          normalized_dtcai: (item.dtcai - Math.min(...lpplAiData.map(d => d.dtcai))) / 
                          (Math.max(...lpplAiData.map(d => d.dtcai)) - Math.min(...lpplAiData.map(d => d.dtcai)))
        }));
        setLpplData(normalizedLpplData);

      } catch (err) {
        setError(err instanceof Error ? err.message : 'Failed to load data. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
        <Typography color="error">{error}</Typography>
      </Box>
    );
  }

  const selectedAssetData = selectedAsset ? assetsData.find(asset => asset.symbol === selectedAsset) : null;

  const getLastFiveYearsData = (data: StockData[] | LPPLAIData[]) => {
    const fiveYearsAgo = new Date();
    fiveYearsAgo.setFullYear(fiveYearsAgo.getFullYear() - 5);
    return data.filter(item => new Date(item.date) >= fiveYearsAgo);
  };

  const chartData = selectedAssetData ? {
    labels: getLastFiveYearsData(selectedAssetData.data)
      .map(item => item.date)
      .reverse(),
    datasets: [
      {
        label: `${selectedAssetData.name} (Normalized)`,
        data: getLastFiveYearsData(selectedAssetData.data)
          .map(item => (item as any).normalized_price)
          .reverse(),
        borderColor: 'rgb(75, 192, 192)',
        backgroundColor: 'rgba(75, 192, 192, 0.1)',
        tension: 0.1,
        fill: true,
      },
      ...(selectedAsset === '^GSPC' ? [{
        label: 'LPPL AI DTC (Normalized)',
        data: getLastFiveYearsData(lpplData)
          .map(item => (item as any).normalized_dtcai)
          .reverse(),
        borderColor: 'rgb(255, 99, 132)',
        backgroundColor: 'rgba(255, 99, 132, 0.1)',
        tension: 0.1,
        fill: true,
      }] : [])
    ],
  } : null;

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top' as const,
      },
      title: {
        display: true,
        text: selectedAssetData 
          ? selectedAsset === '^GSPC'
            ? `Normalized ${selectedAssetData.name} and LPPL AI DTC Comparison (Last 5 Years)`
            : `Normalized ${selectedAssetData.name} (Last 5 Years)`
          : '',
      },
      tooltip: {
        callbacks: {
          label: function(context: any) {
            const label = context.dataset.label || '';
            const value = context.parsed.y;
            return `${label}: ${value.toFixed(4)}`;
          }
        }
      }
    },
    scales: {
      y: {
        beginAtZero: true,
        max: 1,
        title: {
          display: true,
          text: 'Normalized Value'
        }
      },
      x: {
        title: {
          display: true,
          text: 'Date'
        },
        reverse: false // Ensure x-axis goes from left to right
      }
    },
    interaction: {
      intersect: false,
      mode: 'index' as const,
    },
  };

  if (selectedAsset && chartData) {
    return (
      <Box sx={{ 
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        bgcolor: 'background.paper',
        zIndex: 1000,
        p: 3
      }}>
        <Button 
          variant="contained" 
          onClick={() => setSelectedAsset(null)}
          sx={{ mb: 2 }}
        >
          Back to Assets
        </Button>
        <Box sx={{ height: 'calc(100vh - 100px)' }}>
          <Line data={chartData} options={options} />
        </Box>
      </Box>
    );
  }

  return (
    <Container maxWidth="lg">
      <Box sx={{ mt: 4, mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          LPPL AI Analysis
        </Typography>
        <Box 
          component="img"
          src="/yonsei_univ_photo.png"
          alt="Yonsei University"
          sx={{
            width: '100%',
            maxWidth: '1200px',
            height: 'auto',
            mb: 4,
            display: 'block',
            mx: 'auto'
          }}
        />
        <Grid container spacing={2} sx={{ mb: 4 }}>
          {assets.map((asset) => (
            <Grid item xs={6} sm={3} key={asset.symbol}>
              <Card 
                sx={{ 
                  cursor: 'pointer',
                  bgcolor: selectedAsset === asset.symbol ? 'primary.light' : 'background.paper',
                  border: '2px solid #1976d2',
                  '&:hover': {
                    bgcolor: 'primary.light',
                    opacity: 0.8
                  }
                }}
                onClick={() => setSelectedAsset(asset.symbol)}
              >
                <CardContent>
                  <Typography variant="h6" align="center">
                    {asset.name}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Container>
  );
};

export default SP500Return;
