import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { CircularProgress, Box, Typography, Container } from '@mui/material';
import { API_URL } from '../config.ts';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

interface StockData {
  date: string;
  close_price: number;
}

interface LPPLAIData {
  date: string;
  close_price: number;
  dtcai: number;
  color: string;
}

const SP500Return: React.FC = () => {
  const [data, setData] = useState<StockData[]>([]);
  const [lpplData, setLpplData] = useState<LPPLAIData[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        setError(null);

        // Fetch S&P 500 data
        const sp500Response = await fetch(`${API_URL}/api/v1/stock-data/symbol/^GSPC`);
        if (!sp500Response.ok) {
          throw new Error('Failed to fetch S&P 500 data');
        }
        const sp500Data = await sp500Response.json();
        setData(sp500Data);

        // Fetch LPPL AI data
        const lpplResponse = await fetch(`${API_URL}/api/v1/lppl-ai-data/`);
        if (!lpplResponse.ok) {
          throw new Error('Failed to fetch LPPL AI data');
        }
        const lpplAiData = await lpplResponse.json();
        setLpplData(lpplAiData);

      } catch (err) {
        setError(err instanceof Error ? err.message : 'Failed to load data. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
        <Typography color="error">{error}</Typography>
      </Box>
    );
  }

  const chartData = {
    labels: data.map(item => item.date),
    datasets: [
      {
        label: 'S&P 500',
        data: data.map(item => item.close_price),
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1,
      },
      {
        label: 'LPPL AI DTC',
        data: lpplData.map(item => item.dtcai),
        borderColor: 'rgb(255, 99, 132)',
        tension: 0.1,
      }
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top' as const,
      },
      title: {
        display: true,
        text: 'S&P 500 and LPPL AI DTC Comparison',
      },
    },
    scales: {
      y: {
        beginAtZero: false,
      },
    },
  };

  return (
    <Container maxWidth="lg">
      <Box sx={{ mt: 4, mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          S&P 500 and LPPL AI Analysis
        </Typography>
        <Box sx={{ height: '600px', position: 'relative' }}>
          <Line data={chartData} options={options} />
        </Box>
      </Box>
    </Container>
  );
};

export default SP500Return;
